class BL2ArchiData(NamedTuple):
    # reusing this class for both item and location data. most props are only relevant to the locations.
    region: Optional[str] = ""
    level: Optional[int] = None # 0 = pre damage
    description: Optional[str] = "" # unused
    is_gear: bool = False
    other_req_regions: List[str] = []
    req_groups: List[str] = []
    req_items: List[str] = []
    coop_type: int = 0 # 1 = impossible without coop, 2 = difficult without coop
    jump_z_req: int = 0 # unconfirmed jump checks are set to 629
    is_raidboss: bool = False
    associated_gear: str = "" # for items
    item_kind: str = filler # for items

